namespace Eccube\Repository\Master;

use Customize\Entity\Master\BulkBuying;
use Eccube\Repository\AbstractRepository;
use Symfony\Bridge\Doctrine\RegistryInterface;

/**
 * BulkBuyingRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class BulkBuyingRepository extends AbstractRepository
{
    public function __construct(RegistryInterface $registry)
    {
        parent::__construct($registry, BulkBuying::class);
    }

    /**
     * まとめ買いグループの表示順を一つ上げる.
     *
     * @param BulkBuying $BulkBuying
     *
     * @throws \Exception 更新対象のユーザより上位のユーザが存在しない場合.
     */
    public function up(BulkBuying $BulkBuying)
    {
        $sortNo = $BulkBuying->getSortNo();
        $BulkBuying2 = $this->findOneBy(['sort_no' => $sortNo + 1]);

        if (!$BulkBuying2) {
            throw new \Exception(sprintf('%s より上位のまとめ買いグループが存在しません.', $BulkBuying->getId()));
        }

        $BulkBuying->setSortNo($sortNo + 1);
        $BulkBuying2->setSortNo($sortNo);

        $em = $this->getEntityManager();
        $em->persist($BulkBuying);
        $em->persist($BulkBuying2);
        $em->flush();
    }

    /**
     * まとめ買いグループの表示順を一つ下げる.
     *
     * @param BulkBuying $BulkBuying
     *
     * @throws \Exception 更新対象のユーザより下位のユーザが存在しない場合.
     */
    public function down(BulkBuying $BulkBuying)
    {
        $sortNo = $BulkBuying->getSortNo();
        $BulkBuying2 = $this->findOneBy(['sort_no' => $sortNo - 1]);

        if (!$BulkBuying2) {
            throw new \Exception(sprintf('%s より下位のまとめ買いグループが存在しません.', $BulkBuying->getId()));
        }

        $BulkBuying->setSortNo($sortNo - 1);
        $BulkBuying2->setSortNo($sortNo);

        $em = $this->getEntityManager();
        $em->persist($BulkBuying);
        $em->persist($BulkBuying2);
        $em->flush();
    }

    /**
     * 登録.
     *
     * @param BulkBuying $BulkBuying
     */
    public function save($BulkBuying)
    {
        if (!$BulkBuying->getId()) {
            $sortNo = $this->createQueryBuilder('c')
                ->select('COALESCE(MAX(c.sort_no), 0)')
                ->getQuery()
                ->getSingleScalarResult();
            $BulkBuying
                ->setSortNo($sortNo + 1);
        }

        $em = $this->getEntityManager();
        $em->persist($BulkBuying);
        $em->flush();
    }

    /**
     * 削除.
     *
     * @param BulkBuying $BulkBuying
     *
     * @throws ForeignKeyConstraintViolationException 外部キー制約違反の場合
     * @throws DriverException SQLiteの場合, 外部キー制約違反が発生すると, DriverExceptionをthrowします.
     */
    public function delete($BulkBuying)
    {
        $this->createQueryBuilder('c')
            ->update()
            ->set('c.sort_no', 'c.sort_no - 1')
            ->where('c.sort_no > :sort_no')
            ->setParameter('sort_no', $BulkBuying->getSortNo())
            ->getQuery()
            ->execute();

        $em = $this->getEntityManager();

        $em->flush();

        $em->remove($BulkBuying);
        $em->flush();
    }
}
